import React, { Component } from 'react'

export default class Content extends Component {


    constructor(props) {
        super(props)
    
        this.state = {
             //showLoginForm : false,
             componentName : 'Content',
             onClose : false
        }
    }

    renderComponent () {

        const {componentName} = this.state;
    
            switch(componentName) {

                case 'login' : return (
                    <React.Fragment>
                        <NavBar />
                        <LoginForm onClose = {() => {
                        this.setState({
                            componentName : 'NavBar',
                            onClose : true
                            })
                        }} />
                    </React.Fragment>
                 ) 

                case 'subscribe' : return (
                    <React.Fragment>
                        <NavBar />
                        <Subscribe onClose = {() => {
                        this.setState({
                            componentName : 'NavBar',
                            onClose : true
                            })
                        }} />
                    </React.Fragment>
                 ) 
                
                case 'help' : return (
                    <React.Fragment>
                    <NavBar />
                    <Help onClose = {() => {
                    this.setState({
                        componentName : 'NavBar',
                        onClose : true
                    })
                }} />
                    </React.Fragment>
                 ) 

                case 'about' : return (
                <React.Fragment>
                    <NavBar />
                    <About onClose = {() => {
                        this.setState({
                            componentName : 'NavBar',
                            onClose : true
                        })
                    }} />
                </React.Fragment>
                 ) 

                 default : return <NavBar 
                 onShowPage = {
                    (componentName) => {
                        this.setState({
                            componentName : componentName
                        })
                    }
                }
                 />;

            }

    }

    render() {
        return (
            <div>
                {this.renderComponent()}
            </div>
        )
    }
}
