import React, { Component } from 'react'

export default class LoginForm extends Component {

    constructor(props) {
        super(props)

        this.state = {
            username : "",
            password : ""
        }

        this.onSubmit = this.onSubmit.bind(this);
    }

    onSubmit = (event) => {

            event.preventDefault();
            console.log('login ');

    }
    

    render() {

        return (
        
                <div className="app-login-form-inner">
                    <h2 className="form-title">Sign In</h2>
                    
                    <form onSubmit={this.onSubmit}>
                        <div className="app-form-item">
                            <input id="email-id" type="email" placeholder="Enter your Email" name="email" />
                        </div>
                        <div className="app-form-item">
                            <input id="password-id" type="password" placeholder="Enter your Password" name="email" />
                        </div>
                        <div className="app-form-actions">
                            <button className="app-button primary">Login</button>
                            
                        </div>
                    </form>
                    <div className="app-form-forgotten-pass">
                                <a href="">Forgot Password ?</a>
                            </div>
                            <div className="app-form-forgotten-pass">
                                <button onClick={() => {
                                    if(this.props.onShow) {
                                        this.props.onShow('subscribe');
                                    }
                                }} >SIGN UP</button>
                            </div>
                </div>
        )
    }
}
