import React, { Component } from 'react';
import dropIcon from '../dropIcon.svg';
import ButtonFolder from '../buttonAddFolder/ButtonFolder';
import Form from 'react-bootstrap/Form';
import FormScroll from './FormScroll';
import styled from 'styled-components';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faPlusCircle, faChevronCircleDown, faChevronCircleUp} from '@fortawesome/free-solid-svg-icons'
import './Dropzone.css';

export default class Dropzone extends Component {

  constructor(props) {

    super(props);

    this.state = { 
      hightlight: false,
      mailTo : '',
      mailFrom : '',
      message : '', 
      formScroll : false
     };
    this.fileInputRef = React.createRef();

    this.openFileDialog = this.openFileDialog.bind(this);
    this.onFilesAdded = this.onFilesAdded.bind(this);
    this.onDragOver = this.onDragOver.bind(this);
    this.onDragLeave = this.onDragLeave.bind(this);
    this.onDrop = this.onDrop.bind(this);

  }

  openFileDialog() {
    if (this.props.disabled) return;
    this.fileInputRef.current.click();
  }

  onFilesAdded(evt) {
    if (this.props.disabled) return;
    const files = evt.target.files;
    if (this.props.onFilesAdded) {
      const array = this.fileListToArray(files);
      this.props.onFilesAdded(array);
    }
  }

  fileListToArray(list) {
    const array = [];
    for (var i = 0; i < list.length; i++) {
      array.push(list.item(i));
    }
    return array;
  }

  onDragOver(evt) {
    evt.preventDefault();
  
    if (this.props.disabled) return;
  
    this.setState({ hightlight: true });
  }

  onDragLeave() {
    this.setState({ hightlight: false });
  }

  onDrop(event) {
    event.preventDefault();
  
    if (this.props.disabled) return;
  
    const files = event.dataTransfer.files;
    if (this.props.onFilesAdded) {
      const array = this.fileListToArray(files);
      this.props.onFilesAdded(array);
    }
    this.setState({ hightlight: false });
  }

  scrollDown = () => {
    this.setState({
        formScroll : true
    });
}

scrollUp = () => {
    this.setState({
        formScroll : true
    });
}

handleMailToChange = event => {
    this.setState({
        mailTo : event.target.value
    })
}

handleMailFromChange = event => {
    this.setState({
        mailFrom : event.target.value
    })
}

handleMessageChange = event => {
    this.setState({
        message : event.target.value
    })
}

handleSubmit = event => {
    alert(`
        ${this.state.mailTo} 
        ${this.state.mailFrom}
        ${this.state.message}
    `)
    event.preventDefault();
}

  render() {

    const {mailTo, mailFrom, message, formScroll} = this.state;

    let buttonDown = (
      <Button  onClick={this.scrollDown} type="submit">
          <FontAwesomeIcon className="scrollIcon d-flex"
                  icon={faChevronCircleDown}
          />    
      </Button>
  )

  let buttonUp = (
      <Button  onClick={this.scrollUp} type="submit">
          <FontAwesomeIcon className="scrollIcon d-flex"
                  icon={faChevronCircleUp}
          />
      </Button>
  )

    return (
     <div>
       <div
        className={`Dropzone ${this.state.hightlight ? "Highlight" : ""}`}
        onDragOver={this.onDragOver}
        onDragLeave={this.onDragLeave}
        onDrop={this.onDrop}
        onClick={this.openFileDialog}
        style={{ cursor: this.props.disabled ? "default" : "pointer" }}
      >
        <input
          ref={this.fileInputRef}
          className="FileInput"
          type="file"
          multiple
          onChange={this.onFilesAdded}
        />
        <img
          alt="upload"
          className="Icon"
          src={dropIcon}
        />
        <span>Upload Files</span>
      </div>
        <ButtonFolder 
          fileInputRef={this.fileInputRef} 
          onFilesAdded={this.onFilesAdded}
          openFileDialog={this.openFileDialog}
        />

        <Form > 
          <div className="row d-flex justify-content-center">
            <Form.Group className="col-10 mx-auto" controlId="formBasicSend">
              <Form.Control type="email" placeholder="Send to" value={mailTo} onChange={this.handleMailToChange} />
            </Form.Group>
          </div>
          <div className="row d-flex justify-content-center">
            <Form.Group className="col-10 mx-auto" controlId="formBasicEmail">
              <Form.Control type="email" placeholder="Your Email address" value={mailFrom} onChange={this.handleMailFromChange} />
            </Form.Group>
          </div>
          <div className="row d-flex justify-content-center">
            <Form.Group className="col-10 mx-auto" controlId="exampleForm.ControlTextarea1">
              <Form.Control as="textarea" rows="3" placeholder="Message..." value={message} onChange={this.handleMessageChange}/>
            </Form.Group>
          </div>
          {/* <FontAwesomeIcon style={styles} className="scrollIcon col-2 col-md-2 col-lg-3"
          icon={faChevronCircleDown}
          />  */} 
          <div className="row justify-content-center">
            { formScroll ? <FormScroll /> :  buttonDown}    
          </div>
          <div className="row justify-content-center">
            { formScroll && buttonUp }    {/* CHECK IF THE COMPONENT IS GENERATED ENTIRELY */}
          </div>
          <div className="row d-flex justify-content-center">
            <Button variant="primary" type="submit" className="col-8 mb-3 col-sm-8 col-md-8 col-lg-6 justify-content-center">
                Submit
            </Button>
          </div>
        </Form>

    </div>
    );
  }
}
