import React from 'react'
import './ButtonFolder.css'

export default function ButtonFolder({fileInputRef , onFilesAdded, openFileDialog}) {
    return (
        <div className="">
            <button className="buttonFolder" onClick={openFileDialog} >
                Select Folder
            <input 
                directory="" 
                webkitdirectory="" 
                ref={fileInputRef}
                className="FileInput"
                type="file"
                multiple
                onChange={onFilesAdded}
            />
            </button>
        </div>
    )
}
