                 />;

            }

    }
    

    render() {
        return (
            <div className="app-layout">
                {this.renderComponent()}
                <Router history={history}>
                    <Switch>
                        <Route exact path={'/'} component={Home} />
                        <Route exact path={'/share/:id'} component={View} />
