import React, { Component } from 'react';
import {StripeProvider, Elements, CardElement, injectStripe, ReactStripeElements} from 'react-stripe-elements';

export default class Payment extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             selectValue : 'monthly',
             price : 12,
             number : null,
             name : null,
             expire : null,
             cvc : null,
             focus : null
        }

        this.onOptionChange = this.onOptionChange.bind(this);
    }
    onTextFieldChange = (e) => {

        let {user} = this.state;

        const fieldName = e.target.name;
        const fieldValue = e.target.value;

        user[fieldName] = fieldValue;

        this.setState({
            user : user
        })

    }


    onTextFieldChange = (e) => {
        let selectValue = this.state;
        let price = this.state;

        selectValue = e.target.value;

        if(selectValue === 'monthly') {
            price = 12;
        }
        else {
            price = 120;
        }

        console.log('value : ', selectValue);

        this.setState({
            selectValue : selectValue,
            price : price,
        })
    }
    

    render() {

        const {selectValue, price, number, name, expire, cvc, focus} = this.state;

        return (
            <div className="app-payment-component">
                <h1>Select your payment method</h1>
                    <div className="app-payment-component-inner">
                        <div className="app-payment-component-inner-first">
                            <h2>WeTransfer Pro: {selectValue} payment</h2>
                            <select className="app-payment-component-select" id="payment" onChange={this.onTextFieldChange}  name="payment-type">
                                <option value="monthly" >Monthly Payment</option>
                                <option value="yearly" >Yearly Payment</option>
                            </select>
                            
                        </div>
                        {
                            price = 12 ? <div className="app-payment-component-inner-second"><p>${price} USD</p> /month</div> :
                                         <div className="app-payment-component-inner-second"><p>${price} USD</p> /year</div>    
                        }    
                    </div>
                    <div className="app-payment-credit-card">
                        <StripeProvider apikey = "pk_test_pi0A9UqABLxxKC1M4ymZolqa003eBMBAEt" >
                            <Elements>
                                <FormPayment />
                            </Elements>
                        </StripeProvider>
                    </div>
            </div>
        )
    }
}
