import React, { Component } from 'react';
import {CardElement, injectStripe, ReactStripeElements} from 'react-stripe-elements';

class FormPayment extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            fields : {
                name : "",
                amount : ""
        }
        }
    
        this.onTextFieldChange = this.onTextFieldChange.bind(this);
    }
    
    onTextFieldChange = (e) => {

        let {fields} = this.state;

        const fieldName = e.target.name;
        const fieldValue = e.target.value;

        fields[fieldName] = fieldValue;

        this.setState({
            fields : fields
        })

    }


    render() {
        return (
            <div className="app-form-payment-component">
                <form>
                <label>name</label>
                <input type="text" className="" value={this.state.name}  onChange={this.onTextFieldChange} />

                <label>Amount</label>
                <input type="text" className="" value={this.state.amount}  onChange={this.onTextFieldChange} />
                <label>CC Number -- Exp. Date -- CVC</label> 
                <CardElement className="card-element" />
                <button className="">Charge it !</button>
            </form>
            </div>
            
        )
    }

}

export default injectStripe(FormPayment);
