import React from 'react'

export default function ButtonFolder({fileInputRef , onFilesAdded, openFileDialog}) {

    const directory = (directory="") 
    return (
        <div>
            <button onClick={openFileDialog}>
            <input 
                directory
                webkitdirectory="" 
                ref={fileInputRef}
                className="FileInput"
                type="file"
                multiple
                onChange={onFilesAdded}
            />
            </button>
        </div>
    )
}
