import React, { Component } from 'react';
import NavBar from '../components/NavBar';
import LoginForm from '../components/LoginForm';
import Subscribe from '../components/Subscribe';
import About from '../components/About';
import Help from '../components/Help';

export default class Content extends Component {


    constructor(props) {
        super(props)
    
        this.state = {
             onClose : false,
        }
    }

    renderComponent () {

        const {subComponentName} = this.props;

            switch(subComponentName) {

                case 'login' : return (
                    <React.Fragment>
                        <NavBar />
                        <LoginForm onClose = {() => {
                            this.setState({
                                onClose : true
                                })
                            }} />
                    </React.Fragment>
                 ) 

                case 'subscribe' : return (
                    <React.Fragment>
                        <NavBar />
                        <Subscribe onClose = {() => {
                             this.setState({
                                onClose : true
                                })
                            }} />
                    </React.Fragment>
                 ) 
                
                case 'help' : return (
                    <React.Fragment>
                    <NavBar />
                    <Help onClose = {() => {
                             this.setState({
                                onClose : true
                                })
                            }} />
                    </React.Fragment>
                 ) 

                case 'about' : return (
                <React.Fragment>
                    <NavBar />
                    <About onClose = {() => {
                             this.setState({
                                onClose : true
                                })
                            }} />
                </React.Fragment>
                 ) 

                 default : return <Content />;

            }

    }

    render() {
        return (
            <div>
                {this.renderComponent()}
            </div>
        )
    }
}
