import React, { Component } from 'react';
import NavBar from '../components/NavBar';
import LoginForm from '../components/LoginForm';
import Subscribe from '../components/Subscribe';
import About from '../components/About';
import Help from '../components/Help';

export default class Content extends Component {


    constructor(props) {
        super(props)
    
        this.state = {
             onClose : false,
             componentName : null
        }
    }

    static getDerivedStateFromProps(nextProps, prevState){

        const {subComponentName} = this.props;
        const{componentName} = prevState;

    return (componentName : subComponentName)
        
    }

    renderComponent () {

        
        const {onClose, componentName} = this.state;
        
        

        console.log(onClose);

            switch(componentName) {

                case 'login' : return (
                    <React.Fragment>
                        <NavBar />
                        <LoginForm onClose = {() => {
                            this.setState({
                                onClose : true,
                                componentName : null
                                })
                            }} />
                    </React.Fragment>
                 ) 

                case 'subscribe' : return (
                    <React.Fragment>
                        <NavBar />
                        <Subscribe onClose = {() => {
                             this.setState({
                                onClose : true,
                                componentName : null
                                })
                            }} />
                    </React.Fragment>
                 ) 
                
                case 'help' : return (
                    <React.Fragment>
                    <NavBar />
                    <Help onClose = {() => {
                             this.setState({
                                onClose : true,
                                componentName : null
                                })
                            }} />
                    </React.Fragment>
                 ) 

                case 'about' : return (
                <React.Fragment>
                    <NavBar />
                    <About onClose = {() => {
                             this.setState({
                                onClose : true,
                                componentName : null
                                })
                            }} />
                </React.Fragment>
                 ) 

                 default : return onClose ? <Content /> : <NavBar />; 

            }

    }

    render() {
        return (
            <div>
                {this.renderComponent()}
            </div>
        )
    }
}
