import React, { Component } from 'react';
import Cards from 'react-credit-cards';
import 'react-credit-cards/es/styles-compiled.css';

export default class Payment extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             selectValue : 'monthly',
             price : 12,
             duration : 'month'
        }

        this.onOptionChange = this.onOptionChange.bind(this);
    }

    onOptionChange = (e) => {
        let selectValue = this.state;
        let price = this.state;

        selectValue = e.target.value;

        if(selectValue === 'monthly') {
            price = 12;
        }
        else {
            price = 120;
        }

        console.log('value : ', selectValue);

        this.setState({
            selectValue : selectValue,
            price : price,
        })
    }
    

    render() {

        const {selectValue, price, duration} = this.state;

        return (
            <div className="app-payment-component">
                <h1>Select your payment method</h1>
                    <div className="app-payment-component-inner">
                        <div className="app-payment-component-inner-first">
                            <h2>WeTransfer Pro: {selectValue} payment</h2>
                            <select className="app-payment-component-select" id="payment" onChange={this.onOptionChange}  name="payment-type">
                                <option value="monthly" >Monthly Payment</option>
                                <option value="yearly" >Yearly Payment</option>
                            </select>
                            
                        </div>
                        {
                            price = 12 ? <div className="app-payment-component-inner-second"><p>${price} USD</p> /month</div> :
                                         <div className="app-payment-component-inner-second"><p>${price} USD</p> /year</div>    
                        }    
                    </div>
                    <div className="app-payment-credit-card">

                    </div>
            </div>
        )
    }
}
