import React, { Component } from 'react';

export default class Payment extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             selectValue : 'monthly',
             price : 12
        }

        this.onOptionChange = this.onOptionChange.bind(this);
    }

    onOptionChange = (e) => {
        let selectValue = this.state;
        selectValue = e.target.value;

        console.log('value : ', selectValue);

        this.setState({
            selectValue : selectValue
        })
    }
    

    render() {

        const {selectValue, price} = this.state;

        return (
            <div className="app-payment-component">
                <h1>Select your payment method</h1>
                    <div className="app-payment-component-inner">
                        <div className="app-payment-component-inner-left">
                            <h2>WeTransfer Pro {selectValue}</h2>
                            <select id="payment" onChange={this.onOptionChange} name="payment-type">
                                <option value="monthly" >Monthly</option>
                                <option value="yearly" >Yearly</option>
                            </select>
                        </div>
                        <div className="app-payment-component-inner-right">
                            <p>${price} USD</p>/year
                        </div>  
                    </div>
            </div>
        )
    }
}
