import React, { Component } from 'react'

export default class LoginForm extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             isLogin : true
        }
    }
    

    render() {
        return (
            <div className="app-right-components">
                <button className="app-close-button" onClick={() => {
                    if(this.props.onClose){
                        this.props.onClose(true);
                    }
                }} >x</button>
                <div className="app-login-form-inner">
                    <h2 className="form-title">Sign In</h2>
                    
                    <form>
                        <div className="app-form-item">
                            <input id="email-id" type="email" placeholder="Enter your Email" name="email" />
                        </div>
                        <div className="app-form-item">
                            <input id="email-id" type="password" placeholder="Enter your Password" name="email" />
                        </div>
                        <div className="app-form-actions">
                            <button className="app-button primary">Login</button>
                            <div className="app-form-forgotten-pass">
                                <a href="">Forgot Password ?</a>
                            </div>
                            <div className="app-form-forgotten-pass">
                                <a onClick={() => {
                                    this.setState({
                                        isLogin : false
                                    })
                                }} >SIGN UP</a>
                            </div>
                        </div>
                    </form>

                </div>
            </div>
        )
    }
}
