import React from 'react';

const KB = 1024;
const MB = KB * KB;
const TB = KB * KB * KB;

export const formatNumbers = (input, round = true) => {
   
    if(input > KB) {
        return `${input/KB} KB`;
    }
    if (input > MB) {
        return `${input/MB} MB`;
    }
    
}
