import React, { Component } from 'react';
import NavBar from '../components/NavBar';
import LoginForm from '../components/LoginForm';
import Subscribe from '../components/Subscribe';
import About from '../components/About';
import Help from '../components/Help';

export default class Content extends Component {


    constructor(props) {
        super(props)
    
        this.state = {
             onClose : false,
             componentName : null
        }
    }

    static getDerivedStateFromProps(nextProps, prevState) {

        const {subComponentName} = nextProps;
        const {componentName} = prevState;
        console.log('getDerivedStateFromProps');
        return {componentName : subComponentName}

    }

    componentDidUpdate(){
        console.log('componentDidMount');
        this.setState({
            componentName : null
        })
    }

    renderComponent () {

        const {componentName} = this.state;
        const {onClose} = this.state;

        console.log(onClose);
        console.log(componentName);
        
            switch(componentName) {

                case 'login' : return (
                    <React.Fragment>
                        <NavBar />
                        <LoginForm onClose = {() => {
                            this.setState({
                                onClose : true,
                                componentName : null
                                })
                            }} />
                    </React.Fragment>
                 ) 

                case 'subscribe' : return (
                    <React.Fragment>
                        <NavBar />
                        <Subscribe onClose = {() => {
                             this.setState({
                                onClose : true,
                                componentName : null
                                })
                            }} />
                    </React.Fragment>
                 ) 
                
                case 'help' : return (
                    <React.Fragment>
                    <NavBar />
                    <Help onClose = {() => {
                             this.setState({
                                onClose : true,
                                componentName : null
                                })
                            }} />
                    </React.Fragment>
                 ) 

                case 'about' : return (
                <React.Fragment>
                    <NavBar />
                    <About onClose = {() => {
                             this.setState({
                                onClose : true,
                                componentName : null
                                })
                            }} />
                </React.Fragment>
                 ) 

                 default : return onClose ? <NavBar /> : <Content />

            }

    }

    render() {
        return (
            <div>
                {this.renderComponent()}
            </div>
        )
    }
}
