import React, { Component } from 'react';

export default class Payment extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             selectValue : null
        }
    }

    onOptionChange = (e) => {
        let selectValue = this.state;
        
    }
    

    render() {
        return (
            <div className="app-payment-component">
                <h1>Select your payment method</h1>
                    <div className="app-payment-component-inner">
                        <div className="app-payment-component-inner-left">
                            <h2>WeTransfer Pro {}</h2>
                            <select id="payment" name="payment-type">
                                <option value="monthly">Monthly</option>
                                <option value="yearly">Yearly</option>
                            </select>
                        </div>
                        <div className="app-payment-component-inner-right">
                            <p>$120 USD</p>/year
                        </div>  
                    </div>
            </div>
        )
    }
}
