import React, { Component } from 'react'
const _ = require('lodash');

export default class HomeForm extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             form : {
                 files : [],
                 to : '',
                 from : '',
                 message : ''
             },

             errors : {
                 to : null,
                 from : null,
                 message : null
             }
        }

        this._onTextChange = this._onTextChange.bind(this);
        this._onSubmit = this._onSubmit.bind(this);
        this._checkForm = this._checkForm.bind(this);
        this._onFileAdded = this._onFileAdded.bind(this);
    }

    _isEmail = (emailAddress) => {
        const emailRegex = /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
        
        return emailRegex.test(emailAddress);
    }

    _checkForm = (fields = [], callback) => {

        let {form, errors} = this.state;

        const validations = {

            to : [
                {
                    errorMessage : "To is required !",
                    isValid : () => {
                        return form.to.length;
                    }
                },

                {
                    errorMessage : "Email is not valid",
                    isValid : () => {
                        return this._isEmail(form.to);
                    }
                }
            ],

            from : [
                {
                    errorMessage : "From is required !",
                    isValid : () => {
                        return form.from.length;
                    }
                },

                {
                    errorMessage : "Email is not valid",
                    isValid : () => {
                        return this._isEmail(form.from);
                    }
                }
            ]
        }

        _.each(fields, field => {

            let fieldValidations = _.get(validations, field, []); //validations[field]

            errors[field] = null;

            _.each(fieldValidations, fieldValidation => {
                
                const isValid = fieldValidation.isValid();

                if(!isValid) {

                    errors[field] = fieldValidation.errorMessage;
                }
            })
        })

        this.setState({
            errors : errors
        }, () => {
            let isValid = true;
            _.each(errors, (err) => {
                if(err != null) {
                    isValid = false;
                }
            });
            return callback(isValid);
        })
    }
    
    _onTextChange = (event) => {

        let {form} = this.state;
        const fieldName = event.target.name;
        const fieldValue = event.target.value;

        form[fieldName] = fieldValue;
        this.setState({
            form : form
        })
    }

    _onFileAdded = (event) => {

        let files = [];

        _.each(_.get(event, 'target.files', []), file => {
            files.push(file);
        })

        this.setState({
            form : {
                ...this.state.form,
                files : files
            }
        })
        console.log("files", files);
    }

    _onSubmit = (event) => {
        event.preventDefault();
        this._checkForm(['from', 'to'], (isValid) => {
            console.log("the form is validated = ", isValid);
        });
        //console.log(this.state.form);
    }

    render() {

        const {form} = this.state;

        return (
            
            <div className="app-card">
                <form onSubmit={this._onSubmit}>
                    <div className="app-card-header">
                        <div className="app-card-header-inner">
                            <div className="app-files-selected">
                                <div className="app-file-selected-item">
                                    <div className="file-name">apf.jpg</div>
                                    <div className="file-action">X</div>
                                </div>
                            </div>
                            <div className="app-files-selected">
                                <div className="app-file-selected-item">
                                    <div className="file-name">apf.jpg</div>
                                    <div className="file-action">X</div>
                                </div>
                            </div>
                            <div className="app-files-selected">
                                <div className="app-file-selected-item">
                                    <div className="file-name">apf.jpg</div>
                                    <div className="file-action">X</div>
                                </div>
                            </div>
                            <div className="app-file-select-zone">
                                <label htmlFor='input-file'>
                                    <input id='input-file' type="file" onChange={this._onFileAdded} multiple={true} />
                                    <span className="app-upload-icon"/>
                                    <span className="app-upload-description">
                                        Drag and Drop your Files Here
                                    </span>
                                </label>
                            </div>
                        </div>
                    </div>
                    <div className="app-card-content">
                        <div className="app-card-content-inner">
                            <div className="app-form-item">
                                <label htmlFor="to">Send To</label>
                                <input name="to" value={form.to} onChange={this._onTextChange} type="text" placeholder="Email Address" id="to" /> 
                            </div>

                            <div className="app-form-item">
                                <label htmlFor="from">From</label>
                                <input name="from" value={form.from} onChange={this._onTextChange} type="text" placeholder="Your Email Address" id="from" /> 
                            </div>

                            <div className="app-form-item">
                                <label htmlFor="message">Message</label>
                                <textarea id="message" value={form.message} onChange={this._onTextChange} placeholder="Add a note (optional)" name="message" />
                            </div>

                            <div className="app-form-actions">
                                <button className="app-button primary" type="submit">Send</button>
                            </div>
                        </div>
                    </div>
                </form>
            </div>
          
        )
    }
}
