import axios from 'axios';
import  {apiURL} from '../config';
import _ from 'lodash';

export const upload = (form, callback = () => {}) => {

    const url = `${apiURL}/upload`;
    let files = [];
    let data = new FormData();
    let arrayFiles = _.get(form, 'files', []);

    console.log('arrayFiles.length : ', arrayFiles.length);

    for(let i=0;i<arrayFiles.length;i++){

        files = _.get(form, `files[${i}]`, []);

        

        _.each(files, (file) => {
            data.append('files', file);
        });

    }

    console.log('files : ', files);
    
    data.append('to', _.get(form, 'to'));
    data.append('from', _.get(form, 'from'));
    data.append('message', _.get(form, 'message'));

    console.log('data from Upload component : ', data);

    const config = {

        onUploadProgress : (event) => {

            console.log('UPLOAD EVENT from Upload.js ', event);

            return callback({
                type : 'onUploadProgress',
                payload : event
            })
        }

    }

    console.log('files from Upload component : ', files);

    axios.post(url, data, config).then((response) => {

        //upload success
        return callback({
            type : 'success',
            payload : response.data  //payload contains the data
        })

    }).catch((err) => {
        return callback({
            type : 'error',
            payload : err
        })
    });
}
