import React, { Component } from 'react';
import NavBar from '../components/NavBar';
import LoginForm from '../components/LoginForm';
import Subscribe from '../components/Subscribe';
import About from '../components/About';
import Help from '../components/Help';

export default class Content extends Component {


    constructor(props) {
        super(props)

        this.state = {
             onClose : false,
             componentName : props.subComponentName
        }
    }

    renderComponent () {

        const {componentName} = this.state;
        const {onClose} = this.state;

        console.log(onClose);
        console.log(this.props.subComponentName);
        console.log(componentName);
        
            switch(componentName) {

                case 'login' : return (
                    <React.Fragment>
                        <NavBar />
                        <LoginForm onClose = {() => {
                            this.setState({
                                onClose : true,
                                componentName : 'NavBar'
                                })
                            }} />
                    </React.Fragment>
                 ) 

                case 'subscribe' : return (
                    <React.Fragment>
                        <NavBar />
                        <Subscribe onClose = {() => {
                             this.setState({
                                onClose : true,
                                componentName : 'NavBar'
                                })
                            }} />
                    </React.Fragment>
                 ) 
                
                case 'help' : return (
                    <React.Fragment>
                    <NavBar />
                    <Help onClose = {() => {
                             this.setState({
                                onClose : true,
                                componentName : 'NavBar'
                                })
                            }} />
                    </React.Fragment>
                 ) 

                case 'about' : return (
                <React.Fragment>
                    <NavBar />
                    <About onClose = {() => {
                             this.setState({
                                onClose : true,
                                componentName : 'NavBar'
                                })
                            }} />
                </React.Fragment>
                 ) 

                 default : return (
                    onClose ? <NavBar onClose = {() => {
                       this.setState({
                          onClose : false,
                          componentName : null
                          })
                      }} /> : null
                 )
            }
    }

    render() {
        return (
            <div>
                {this.renderComponent()}
            </div>
        )
    }
}
