import React, { Component } from 'react'
import Header from '../components/Header';
import HomeForm from '../components/HomeForm';
import HomeUpload from '../components/HomeUpload';
import HomeUploadSent from '../components/HomeUploadSent';
import _ from 'lodash';
export default class Home extends Component {

    constructor(props){

        super(props); 

        this.state = {
            componentName : 'HomeForm',
            data : null,
            uploadEvent : null
        }

        this._renderComponent = this._renderComponent.bind(this);
    }

    _renderComponent = () => {

        const {componentName, data, uploadEvent} = this.state;

        switch(componentName) {

            case 'HomeUpload' : 
                return <HomeUpload event={uploadEvent} data={data} />

            case 'HomeUploadSent' :
                return <HomeUploadSent />

            default : 
                return (<HomeForm 
                    
                    onUploadEvent = {(event) => {
                        
                        let componentNameToSet = componentName;
                        if(_.get(event, 'type') === 'success'){
                            componentNameToSet = 'HomeUploadSent';
                        console.log("onUploadEvent", event);
                        this.setState({
                            uploadEvent : event,
                            componentName : componentNameToSet
                        })

                        }

                        
                    }}

                    onUploadBegin = { (data) => {
                        console.log("onUploadBegin");
                    this.setState({
                        data : data,
                        componentName : 'HomeUpload'
                    })
                   }
                } />);

        }

    }



    render() {
        return (
            <div className="app-container"> 
                <Header />
                <div className="app-content">
                    {this._renderComponent()}
                </div>
            </div>
        )
    }
}
