import React, { Component } from 'react';
import NavBar from '../components/NavBar';
import LoginForm from '../components/LoginForm';
import Subscribe from '../components/Subscribe';
import About from '../components/About';
import Help from '../components/Help';

export default class Content extends Component {


    constructor(props) {
        super(props)
    
        this.state = {
             onClose : false,
             content : null
        }
    }

    renderComponent () {

        const {content} = this.state;
    
            switch(content) {

                case 'login' : return (
                    <React.Fragment>
                        <NavBar />
                        <LoginForm onClose = {() => {
                        this.setState({
                            componentName : 'NavBar',
                            onClose : true
                            })
                        }} />
                    </React.Fragment>
                 ) 

                case 'subscribe' : return (
                    <React.Fragment>
                        <NavBar />
                        <Subscribe onClose = {() => {
                        this.setState({
                            componentName : 'NavBar',
                            onClose : true
                            })
                        }} />
                    </React.Fragment>
                 ) 
                
                case 'help' : return (
                    <React.Fragment>
                    <NavBar />
                    <Help onClose = {() => {
                    this.setState({
                        componentName : 'NavBar',
                        onClose : true
                    })
                }} />
                    </React.Fragment>
                 ) 

                case 'about' : return (
                <React.Fragment>
                    <NavBar />
                    <About onClose = {() => {
                        this.setState({
                            componentName : 'NavBar',
                            onClose : true
                        })
                    }} />
                </React.Fragment>
                 ) 

                 default : return <Content 
                 onShowPage = {
                    (componentName) => {
                        this.setState({
                            componentName : componentName
                        })
                    }
                }
                 />;

            }

    }

    render() {
        return (
            <div>
                {this.renderComponent()}
            </div>
        )
    }
}
