import React, { Component } from 'react'
import Dropzone from '../dropzone/Dropzone';
import checkCircle from '../checkCircle.svg';
import Progress from '../progress/Progress';
import Form from 'react-bootstrap/Form';
import FormScroll from './FormScroll';
import styled from 'styled-components';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faPlusCircle, faChevronCircleDown, faChevronCircleUp} from '@fortawesome/free-solid-svg-icons'

import './Upload.css'

export default class Upload extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            files: [],
            uploading: false,
            uploadProgress: {},
            successfullUploaded: false,
            mailTo : '',
            mailFrom : '',
            message : '', 
            formScroll : false
          }

          this.onFilesAdded = this.onFilesAdded.bind(this);
          this.uploadFiles = this.uploadFiles.bind(this);
          this.sendRequest = this.sendRequest.bind(this);
          this.renderActions = this.renderActions.bind(this);
    }

    scrollDown = () => {
      this.setState({
          formScroll : true
      });
  }
  
  scrollUp = () => {
      this.setState({
          formScroll : true
      });
  }

  handleMailToChange = event => {
    this.setState({
        mailTo : event.target.value
    })
}

handleMailFromChange = event => {
    this.setState({
        mailFrom : event.target.value
    })
}

handleMessageChange = event => {
    this.setState({
        message : event.target.value
    })
}

handleSubmit = event => {
    alert(`
        ${this.state.mailTo} 
        ${this.state.mailFrom}
        ${this.state.message}
    `)
    event.preventDefault();
}

    onFilesAdded(files) {
        this.setState(prevState => ({
          files: prevState.files.concat(files)
        }));
      }

      renderProgress(file) {
        const uploadProgress = this.state.uploadProgress[file.name];
        if (this.state.uploading || this.state.successfullUploaded) {
          return (
            <div className="ProgressWrapper">
              <Progress progress={uploadProgress ? uploadProgress.percentage : 0} />
              <img
                className="CheckIcon"
                alt="done"
                src={checkCircle}
                style={{
                  opacity:
                    uploadProgress && uploadProgress.state === "done" ? 0.5 : 0
                }}
              />
            </div>
          );
        }
      }

      renderActions() {
        if (this.state.successfullUploaded) {
          return (
            <button
              onClick={() =>
                this.setState({ files: [], successfullUploaded: false })
              }
            >
              Clear
            </button>
          );
        } else {
          return (
            <button
              disabled={this.state.files.length < 0 || this.state.uploading}
              onClick={this.uploadFiles}
            >
              Upload
            </button>
          );
        }
      }

      async uploadFiles() {
        this.setState({ uploadProgress: {}, uploading: true });
        const promises = [];
        this.state.files.forEach(file => {
          promises.push(this.sendRequest(file));
        });
        try {
          await Promise.all(promises);
      
          this.setState({ successfullUploaded: true, uploading: false });
        } catch (e) {
          this.setState({ successfullUploaded: true, uploading: false });
        }
      }

      sendRequest(file) {
        return new Promise((resolve, reject) => {
          const req = new XMLHttpRequest();

          req.upload.addEventListener("progress", event => {
            if (event.lengthComputable) {
             const copy = { ...this.state.uploadProgress };
             copy[file.name] = {
              state: "pending",
              percentage: (event.loaded / event.total) * 100
             };
             this.setState({ uploadProgress: copy });
            }
           });
            
           req.upload.addEventListener("load", event => {
            const copy = { ...this.state.uploadProgress };
            copy[file.name] = { state: "done", percentage: 100 };
            this.setState({ uploadProgress: copy });
            resolve(req.response);
           });
            
           req.upload.addEventListener("error", event => {
            const copy = { ...this.state.uploadProgress };
            copy[file.name] = { state: "error", percentage: 0 };
            this.setState({ uploadProgress: copy });
            reject(req.response);
           });
      
          const formData = new FormData();
          formData.append("file", file, file.name);
      
          req.open("POST", "http://localhost:8000/upload");
          req.send(formData);
        });
      }
    

    render() {

      const {mailTo, mailFrom, message, formScroll} = this.state;

    let buttonDown = (
      <Button  onClick={this.scrollDown} type="submit">
          <FontAwesomeIcon className="scrollIcon d-flex"
                  icon={faChevronCircleDown}
          />    
      </Button>
  )

  let buttonUp = (
      <Button  onClick={this.scrollUp} type="submit">
          <FontAwesomeIcon className="scrollIcon d-flex"
                  icon={faChevronCircleUp}
          />
      </Button>
  )

        return (
                <div className="Upload row mx-auto d-flex">
                    <span className="Title mx-auto col-10">Upload Files</span>
                    <div className="row Files">
                        {this.state.files.map(file => {
                        return (
                            <div key={file.name} className="Row">
                            <span className="Filename">{file.name}</span>
                            {this.renderProgress(file)}
                            </div>
                        );
                        })}
                    </div>
                    <Dropzone className="row d-flex mx-auto"
                        onFilesAdded={this.onFilesAdded}
                        disabled={this.state.uploading || this.state.successfullUploaded}
                      />
                    <div className="content">
                      <Form className="container" > 
                      <div className="row d-flex justify-content-center">
                        <Form.Group className="col-10 mx-auto" controlId="formBasicSend">
                          <Form.Control type="email" placeholder="Send to" value={mailTo} onChange={this.handleMailToChange} />
                        </Form.Group>
                      </div>
                      <div className="row d-flex justify-content-center">
                        <Form.Group className="col-10 mx-auto" controlId="formBasicEmail">
                          <Form.Control type="email" placeholder="Your Email address" value={mailFrom} onChange={this.handleMailFromChange} />
                        </Form.Group>
                      </div>
                      <div className="row d-flex justify-content-center">
                        <Form.Group className="col-10 mx-auto" controlId="exampleForm.ControlTextarea1">
                          <Form.Control as="textarea" rows="3" placeholder="Message..." value={message} onChange={this.handleMessageChange}/>
                        </Form.Group>
                      </div>
                      {/* <FontAwesomeIcon style={styles} className="scrollIcon col-2 col-md-2 col-lg-3"
                      icon={faChevronCircleDown}
                      />  */} 
                      <div className="row justify-content-center">
                        { formScroll ? <FormScroll /> :  buttonDown}    
                      </div>
                      <div className="row justify-content-center">
                        { formScroll && buttonUp }    {/* CHECK IF THE COMPONENT IS GENERATED ENTIRELY */}
                      </div>
                      <div className="row d-flex justify-content-center">
                        <Button variant="primary" type="submit" className="col-8 mb-3 col-sm-8 col-md-8 col-lg-6 justify-content-center">
                            Submit
                        </Button>
                      </div>
                    </Form>
                    {/*<div className="Actions" >{this.renderActions()}</div>*/}
                </div>
                </div>
        )
    }
}

const Button = styled.button `

`;
