import React, { Component } from 'react';
import countries from '../helpers/countries';

export default class SearchFilterCountry extends Component {

  constructor(props) {
    super(props)
  
    this.state = {
       suggestions : []
    }
  }
  
  onTextChanged = (e) => {

    const value = e.target.value;
    let suggestions = [];
    if(value > 0) {
      const regex = new RegExp(`^${value}`, 'i');
      suggestions = countries.sort().filter(v => v.test(regex));
    }

    if(value.length === 0) {
      this.setState(() => ({
        suggestions : []
      }));
    }
    else {
      this.setState(() => ({suggestions}));
    }

  }

  render() {

    return (
      <div>
          <input onChange={this.onTextChanged} type='text' />
          <ul>
            {countries.map( item => <li>{item.name}</li>) }
          </ul> 
      </div>
    )
  }
}
