import React, { Component } from 'react'
import Header from '../components/Header';
import HomeForm from '../components/HomeForm';
import HomeUpload from '../components/HomeUpload';

export default class Home extends Component {

    constructor(props){

        super(props); 

        this.state = {
            componentName : 'HomeUpload',
            data : null,
            uploadEvent : null
        }

        this._renderComponent = this._renderComponent.bind(this);
    }

    _renderComponent = () => {

        const {componentName, data} = this.state;

        switch(componentName) {

            case 'HomeUpload' : 
                return <HomeUpload data={data} />;

            default : 
                return (<HomeForm onUploadBegin = { (event) => {

                    this.setState({
                        data : event,
                        componentName : 'HomeUpload'
                    })
                    console.log("event passed from child component HomeForm", event);
                }}/>);

        }

    }



    render() {
        return (
            <div className="app-container"> 
                <Header />
                <div className="app-content">
                    {this._renderComponent()}
                </div>
            </div>
        )
    }
}
